import React, { useState } from 'react';
import { PlusIcon, DocumentTextIcon, ArchiveBoxIcon, TagIcon, ChevronDownIcon } from '@heroicons/react/24/outline';

interface HeaderProps {
  activeTab: 'active' | 'archived';
  onTabChange: (tab: 'active' | 'archived') => void;
  onNewNote: () => void;
  onManageCategories: () => void;
}

const Header: React.FC<HeaderProps> = ({ activeTab, onTabChange, onNewNote, onManageCategories }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  return (
    <header className="bg-white border-b border-gray-200 sticky top-0 z-40">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex flex-col lg:flex-row lg:justify-between lg:items-center gap-4">
          {/* Logo and Title */}
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-br from-primary-500 to-primary-600 rounded-lg flex items-center justify-center">
                <DocumentTextIcon className="w-5 h-5 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900">Notes App</h1>
                <p className="text-sm text-gray-500 hidden sm:block">Organize your thoughts</p>
              </div>
            </div>
          </div>

          <div className="flex flex-col sm:flex-row sm:items-center gap-3 lg:gap-4">
            {/* Navigation Tabs */}
            <div className="flex items-center space-x-1 bg-gray-100 rounded-lg p-1">
              <button
                onClick={() => onTabChange('active')}
                className={`px-3 sm:px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
                  activeTab === 'active'
                    ? 'bg-white text-primary-700 shadow-sm'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <div className="flex items-center space-x-1 sm:space-x-2">
                  <DocumentTextIcon className="w-4 h-4" />
                  <span className="hidden sm:inline">Active Notes</span>
                  <span className="sm:hidden">Active</span>
                </div>
              </button>
              <button
                onClick={() => onTabChange('archived')}
                className={`px-3 sm:px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
                  activeTab === 'archived'
                    ? 'bg-white text-primary-700 shadow-sm'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <div className="flex items-center space-x-1 sm:space-x-2">
                  <ArchiveBoxIcon className="w-4 h-4" />
                  <span className="hidden sm:inline">Archived</span>
                  <span className="sm:hidden">Archive</span>
                </div>
              </button>
            </div>

            {/* Actions */}
            <div className="flex items-center gap-2">
              {/* Categories Button */}
              <button
                onClick={onManageCategories}
                className="btn btn-secondary btn-sm sm:btn-md flex items-center space-x-1 sm:space-x-2"
              >
                <TagIcon className="w-4 h-4" />
                <span className="hidden sm:inline">Categories</span>
              </button>

              {/* New Note Button */}
              <button
                onClick={onNewNote}
                className="btn btn-primary btn-sm sm:btn-md flex items-center space-x-1 sm:space-x-2 shadow-sm"
              >
                <PlusIcon className="w-4 h-4 sm:w-5 sm:h-5" />
                <span className="hidden sm:inline">New Note</span>
                <span className="sm:hidden">New</span>
              </button>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
