import React from 'react';
import { Note } from '../../types/note.types';
import { PencilIcon, TrashIcon, ArchiveBoxIcon, ArchiveBoxXMarkIcon } from '@heroicons/react/24/outline';
import CategoryBadge from '../categories/CategoryBadge';

interface NoteCardProps {
  note: Note;
  onEdit: (note: Note) => void;
  onDelete: (note: Note) => void;
  onArchive: (note: Note, isArchived: boolean) => void;
}

const NoteCard: React.FC<NoteCardProps> = ({ note, onEdit, onDelete, onArchive }) => {
  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const truncateText = (text: string, maxLength: number) => {
    if (text.length <= maxLength) return text;
    return text.substring(0, maxLength) + '...';
  };

  return (
    <div className="card card-hover p-6 group">
      {/* Header */}
      <div className="flex items-start justify-between mb-3">
        <h3 className="text-lg font-semibold text-gray-900 line-clamp-2 group-hover:text-primary-700 transition-colors duration-200">
          {note.title}
        </h3>

        {/* Actions */}
        <div className="flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200 ml-4">
          <button
            onClick={() => onEdit(note)}
            className="p-2 text-gray-400 hover:text-primary-600 hover:bg-primary-50 rounded-lg transition-colors duration-200"
            title="Edit note"
          >
            <PencilIcon className="w-4 h-4" />
          </button>

          <button
            onClick={() => onArchive(note, !note.is_archived)}
            className="p-2 text-gray-400 hover:text-amber-600 hover:bg-amber-50 rounded-lg transition-colors duration-200"
            title={note.is_archived ? 'Unarchive note' : 'Archive note'}
          >
            {note.is_archived ? (
              <ArchiveBoxXMarkIcon className="w-4 h-4" />
            ) : (
              <ArchiveBoxIcon className="w-4 h-4" />
            )}
          </button>

          <button
            onClick={() => onDelete(note)}
            className="p-2 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors duration-200"
            title="Delete note"
          >
            <TrashIcon className="w-4 h-4" />
          </button>
        </div>
      </div>

      {/* Content */}
      {note.content && (
        <p className="text-gray-600 text-sm mb-4 line-clamp-3 leading-relaxed">
          {truncateText(note.content, 150)}
        </p>
      )}

      {/* Categories */}
      {note.categories && note.categories.length > 0 && (
        <div className="flex flex-wrap gap-2 mb-4">
          {note.categories.map((category) => (
            <CategoryBadge key={category.id} category={category} size="sm" />
          ))}
        </div>
      )}

      {/* Footer */}
      <div className="flex items-center justify-between pt-4 border-t border-gray-100">
        <div className="text-xs text-gray-500">
          <span>Updated {formatDate(note.updated_at)}</span>
        </div>

        {note.is_archived && (
          <div className="flex items-center text-xs text-amber-600 bg-amber-50 px-2 py-1 rounded-full">
            <ArchiveBoxIcon className="w-3 h-3 mr-1" />
            Archived
          </div>
        )}
      </div>
    </div>
  );
};

export default NoteCard;
